import "./App.css";
import styled from "styled-components";
import Carousel from "react-multi-carousel";
import mladaDama from "./assets/images/songs/mladaDama.jpg";
import { useState } from "react";
import  fbIcon from './assets/images/icons/fb.png'
import  ytIcon from './assets/images/icons/yt.png'
import  gmIcon from './assets/images/icons/gm.png'

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5,
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3,
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
  },
};

const panfuSongs = [
  {
    title: "Mlada dama",
    image: mladaDama,
    videoElement: (
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/gDR_qBWy2hA"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    ),
  },
  {
    title: "Mlada dama",
    image: mladaDama,
    videoElement: (
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/gDR_qBWy2hA"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    ),
  },
  {
    title: "Mlada dama",
    image: mladaDama,
    videoElement: (
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/gDR_qBWy2hA"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    ),
  },
  {
    title: "Mlada dama",
    image: mladaDama,
    videoElement: (
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/gDR_qBWy2hA"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    ),
  },
  {
    title: "Mlada dama",
    image: mladaDama,
    videoElement: (
      <iframe
        width="560"
        height="315"
        src="https://www.youtube.com/embed/gDR_qBWy2hA"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    ),
  },
];

function App() {
  const [selectedSong, setSelectedSong] = useState();

  return (
    <div className="App">
      <nav>
        <ul>
          <li data-selected>home</li>
          <li>band</li>
          <li>music</li>
          <li>contact</li>
        </ul>
      </nav>
      <Title>
        <div>
          <h1>PANFU</h1>
          <h2>Pick A Name For Us</h2>
          <Contacts>
            <a><img src={fbIcon} /></a>
            <a><img src={ytIcon} /></a>
            <a><img src={gmIcon} /></a>
          </Contacts>
        </div>
      </Title>
      <section>
        <Carousel responsive={responsive}>
          {panfuSongs.map((song, i) => {
            return (
              <SongItem key={i} onClick={() => setSelectedSong(i)}>
                {selectedSong === i ? (
                  song.videoElement
                ) : (
                  <>
                    <img src={song.image} alt={song.title} />
                    <div>{song.title}</div>
                  </>
                )}
              </SongItem>
            );
          })}
        </Carousel>
      </section>
    </div>
  );
}

export default App;

const Title = styled.section`
  color: white;
  height: calc(100vh - 112px);
  padding: auto;
  position: relative;
  & > div {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    & > h1 {
      font-size: 300px;
      padding: 0;
      margin: 0;
      transform: translateY(30px);
    }
    & > h2 {
      font-size: 110px;
      padding: 0;
      margin: 0;
      font-weight: normal;
      transform: translateY(-30px);
    }
  }
`;

const SongItem = styled.div`
  height: 35vh;
  position: relative;
  background-color: black;
  cursor: pointer;
  overflow: hidden;
  & > img {
    object-fit: cover;
    object-position: center;
    height: 100%;
    width: 100%;
    transition: transform 0.7s ease-in-out;
    &:hover {
      transform: scale(1.2);
    }
  }
  & > div {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translate(-50%, 50%);
    background-color: #00000099;
    padding: 8px 32px;
  }
  & iframe {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
  }
`;


const Contacts = styled.div`
display: flex;
align-items: center;

& img {
  width: 32px;
}
`
