import { useState, useEffect } from "react";
import styled from "styled-components";

export default function Navv() {
  const navbarItems = ["home", "contact", "music", "band"];

  const [selectedItem, setSelectedItem] = useState("home");

  const [classSticky, setClassSticky] = useState('sticky')

  useEffect(() => {
    const onScroll = (event) => {
      const height = Math.round(
        (event.target.scrollTop / window.innerHeight) * 100
      );
      console.log(height);
      if (height > 150) {
        setSelectedItem("band");
        setClassSticky('unset')
      } else if (height > 75) {
        setSelectedItem("music");
      } else if (height > 50) {
        setSelectedItem("contact");
      } else {
        setSelectedItem("home");
      }
    };
    document.getElementById("root").addEventListener("scroll", onScroll);
    return () =>
      document.getElementById("root").removeEventListener("scroll", onScroll);
  }, []);

  return (
    <NavPanfu>
      <ul>
        {navbarItems.map((item, i) => {
          return (
            <li data-selected={item === selectedItem} key={i}>
              {item}
            </li>
          );
        })}
      </ul>
    </NavPanfu>
  );
}


const NavPanfu = styled.nav`
  color: white;
  height: 80px;
  padding: 16px;
  @media screen and (max-width: 464px) {
    position: sticky;
    top: 0;
    z-index: 1;
    background-color: #000000bb;
    height: 20px;
    margin-bottom: 60px;
  }
  & > ul {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
    margin: 0;
    padding: 0;
    @media screen and (max-width: 464px) {
      gap: 16px;
    }
    & > li {
      list-style: none;
      text-transform: uppercase;
      font-size: 1.5rem;
      opacity: 0.8;
      @media screen and (max-width: 464px) {
        font-size: 1rem;
      }
    }
    & > li[data-selected=true] {
      font-weight: bolder;
      opacity: 1;
    }
  }
`;
